mapper.update:
update bs_personmanage
set isenable = #{isenable,jdbcType=BIT}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select 

from bs_personmanage where 

	 userid=#{userid,jdbcType=VARCHAR} 


  and isenable = #{isenable,jdbcType=BIT}

select 
,CASE WHEN userid=userparentid THEN 1 ELSE 0 END rowid
from bs_personmanage where 1=1

	and userid=#{userid,jdbcType=VARCHAR} 


  and isenable = #{isenable,jdbcType=BIT}


	and userparentid = #{userparentid,jdbcType=VARCHAR}

order by rowid desc
